#include<stdio.h>
#include<limits.h>
#include<float.h>
#include<inttypes.h>
int main()
 {
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n ");
       printf("            Data Type                       Size                      Format Specifier                            Ranges               \n ");
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n ");
       printf("               char                          %u                             %%c                               %d to %d \n", sizeof(char),SCHAR_MAX,SCHAR_MIN);
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n ");
       printf("          unsigned char                      %u                             %%c                               %d to %d \n", sizeof(unsigned char),0,UCHAR_MAX);
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n ");
       printf("            short int                        %u                             %%hd                              %hd to %hd \n", sizeof(short int),32767,(-SHRT_MAX-1));
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n ");
       printf("       unsigned short int                    %u                             %%hu                              %hu to %hu \n", sizeof(unsigned short int),SCHAR_MAX,SCHAR_MIN);
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n ");
       printf("               int                           %u                             %%d                               %d to %d \n", sizeof(int),2147483647,(-INT_MAX-1));
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n ");
       printf("          unsigned int                       %u                             %%u                               %u to %u \n", sizeof(unsigned int),0,0xFFFFFFFF);
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n ");
       printf("            long int                         %u                             %%ld                              %ld to %ld \n", sizeof(long int),2147483647L,(-LONG_MAX-1));
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n ");
       printf("       unsigned long int                     %u                             %%lu                              %lu to %lu \n", sizeof(unsigned long int),0xFFFFFFFFUL,0);
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n ");
       printf("            float                            %u                             %%f                                %f to %f  \n", sizeof(float),FLT_MIN,FLT_MAX);
       printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n");
       printf("            double                            %u                             %%g                                %g to %g  \n",sizeof(double), DBL_MIN,DBL_MAX);
	   printf("------------------------------------------------------------------------------------------------------------------------------------------------------------------------- \n");
       printf("   Scientic Notation(double)                  %u                             %%E                                %E to %E  \n",sizeof(double),DBL_MIN,DBL_MAX);
	   printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n");
	   printf("    Exponential Power(float)                  %u                             %%e                                %e to %e  \n", sizeof(float),FLT_MIN,FLT_MAX);
	   printf("--------------------------------------------------------------------------------------------------------------------------------------------------------------------------\n");
    return 0;

}
 

